def test_SHIM_deserialise_from_terracoind(terracoind_proposal_hex, terracoind_superblock_hex):
    assert terracoinlib.SHIM_deserialise_from_terracoind(terracoind_proposal_hex) == ''
    assert terracoinlib.SHIM_deserialise_from_terracoind(terracoind_superblock_hex) == ''


def test_SHIM_serialise_for_terracoind(sentinel_proposal_hex, sentinel_superblock_hex):
    assert terracoinlib.SHIM_serialise_for_terracoind(sentinel_proposal_hex) == ''
    assert terracoinlib.SHIM_serialise_for_terracoind(sentinel_superblock_hex) == ''
